			shortReader->SetImageIO ( d->io );
			shortReader->SetFileName ( paths[0].toAscii().constData() );
			dtkdata->setData ( shortReader->GetOutput() );
			try {
				shortReader->Update();
			}
			catch (itk::ExceptionObject &e) {
				qDebug() << e.GetDescription();
				return false;
			}
